import maya.app.flux.ae.singleton as singleton
import maya.app.flux.ae.utils as futils
import maya.mel as mel
import maya.cmds as cmds

def registerTemplate(classPath, nodeType):
    singleton.registeredTemplates[nodeType] = classPath
    mel.eval(futils.wrapAETemplate(nodeType))

def registeredTypes():
    return singleton.registeredTemplates.keys()

def addCustom(obj):
    create, replace = futils.getAEEventFuncs()
    cmds.editorTemplate(create, replace, obj.instanceKey, callCustom=True)# ===========================================================================
