from maya.app.renderSetup.lightEditor.model.light import LightItem
from maya.app.renderSetup.lightEditor.model.group import LightGroup


import maya.app.renderSetup.lightEditor.views.proxy as proxy
import maya.app.renderSetup.lightEditor.views.proxyFactory as proxyFactory

proxyEntries = {
    LightItem.kTypeName: proxy.LightItemProxy,
    LightGroup.kTypeName: proxy.LightGroupProxy,
}

def initialize(mplugin):
    for entry in proxyEntries.iteritems():
        proxyFactory.register(*entry)

def uninitialize(mplugin):
    for entry in proxyEntries:
        proxyFactory.unregister(entry)
