import maya.app.renderSetup.model.jsonTranslatorUtils as jsonTranslatorUtils

import json

PARENT_TYPE_NAME = "parentTypeName"

class ClipboardData(object):
    def __init__(self, typeName, parentTypeName):
        self._typeName = typeName
        self._parentTypeName = parentTypeName
    
    def typeName(self):
        # typeName was not made a property for interface compatibility with render setup node classes.
        return self._typeName

    @property
    def parentTypeName(self):
        return self._parentTypeName

def getClipboardDataFromJson(clipBoardJson):
    # Returns the clipboard information from the provided clipboard json data.
    objList = json.loads(clipBoardJson)
    typeName = jsonTranslatorUtils.getTypeNameFromDictionary(objList[0])
    parentTypeName = objList[0][PARENT_TYPE_NAME]
    data = ClipboardData(typeName, parentTypeName)
    return objList, data
