import maya
maya.utils.loadStringResourcesForModule(__name__)

'''
This file contains OptionVar implementations for all of the caching preferences that do not
have separate Maya state information. (i.e. those whose value is solely contained within
an optionVar value.)

Keeping them in a separate file like this allows both the CacheEvaluatorManager and the
CachePreferences to access them without recursive imports.
'''
from maya.app.prefs.OptionVar import OptionVar, OPTION_VAR_TYPE_BOOL, OPTION_VAR_TYPE_FLOAT, OPTION_VAR_TYPE_INT
from maya.common.utils import Singleton

__all__ = [ 'CachePreferenceHud'
          , 'CachePreferenceResourceGuard'
          , 'CachePreferenceMemoryThreshold'
          , 'CachePreferenceDiscardFramesOutOfRange'
          , 'CachePreferenceShowCacheStatus'
          , 'CachePreferenceShowWarningFrames'
          , 'CachePreferenceShowInvalidatedFrames'
          , 'CachePreferenceShowSubframes'
          , 'CachePreferenceTimesliderBarHeight'
          , 'CachePreferenceTimesliderBarSpacing'
          , 'CachePreferenceTimesliderBarPosition'
          , 'CachePreferenceShowWarningMessages'
          ]

#======================================================================
class CachePreferenceHud(OptionVar):
    '''
    Class containing the information for the "caching playback HUD visibility" preference.
    '''
    __metaclass__ = Singleton
    ov_id = 'cacheVisibility' # optionVar name and unique preference ID

    def __init__(self):
        ''' Initialize the preference interface '''
        super( CachePreferenceHud, self ).__init__(self.ov_id, OPTION_VAR_TYPE_BOOL, False)
        self.title = maya.stringTable['y_cache_optionvar_states.kCacheHUDText' ]
        self.info  = maya.stringTable['y_cache_optionvar_states.kCacheHUDTip'  ]

