class CachePreferenceMemoryThreshold(OptionVar):
    '''
    Class containing the information for the "caching playback resource guard memory threshold" preference
    '''
    __metaclass__ = Singleton
    ov_id = 'cachedPlaybackMemoryThreshold' # optionVar name and unique preference ID

    def __init__(self):
        ''' Initialize the preference interface '''
        super( CachePreferenceMemoryThreshold, self ).__init__(self.ov_id, OPTION_VAR_TYPE_FLOAT, 50.0)
        self.title = maya.stringTable['y_cache_optionvar_states.kMemoryThresholdText' ]
        self.info  = maya.stringTable['y_cache_optionvar_states.kMemoryThresholdTip'  ]

    @staticmethod
    def ranges():
        ''' :return 4-tuple with the min/max/soft_min/soft_max values for the threshold'''
        return (0, 100, 0, 200)

