#======================================================================
class CachePreferenceTimesliderBarPosition(OptionVar):
    '''
    Class containing the information for the "caching playback timeslider bar position" preference
    '''
    __metaclass__ = Singleton
    ov_id = 'cachedPlaybackPosition' # optionVar name and unique preference ID

    # This could be a dictionary but it's important to preserve the ordering so it's a list of lists.
    DATA = [ [ 'above'  , maya.stringTable['y_cache_optionvar_states.kBarPositionAbove'    ]
                        , maya.stringTable['y_cache_optionvar_states.kBarPositionAboveTip' ]
             ]
           , [ 'below' , maya.stringTable['y_cache_optionvar_states.kBarPositionBelow'    ]
                       , maya.stringTable['y_cache_optionvar_states.kBarPositionBelowTip' ]
             ]
           ]
    # The first element of the list is treated as a dictionary key
    KEYS = [data[0] for data in DATA]

    def __init__(self):
        ''' Initialize the preference interface '''
        super( CachePreferenceTimesliderBarPosition, self ).__init__(self.ov_id, self.KEYS, self.KEYS[1])
        self.title = maya.stringTable['y_cache_optionvar_states.kTimesliderBarPositionText' ]
        self.info  = maya.stringTable['y_cache_optionvar_states.kTimesliderBarPositionTip'  ]
        self.lookup = { mode : i+1 for i, mode in enumerate(self.KEYS) }

    def find_index(self, value):
        '''Find the index in the DATA list of the given enum value'''
        return self.lookup[value]

